function simple_tooltip(t,e){$(t).each(function(){var t=$(this).data("day-number");if($(this).find(".work").text().search("work")>0){$("body").append("<div class='toltip-calendar "+e+"' id='"+e+t+"'>"+$("#tooltip-block"+t).html()+"</div>"),console.log("#"+e+t);var n=$("#"+e+t);$(this).mouseover(function(){n.css({left:$(this).offset().left+$(this).width()+9,top:$(this).offset().top}),$(".tooltip-arrow").css({left:-34,top:34}),n.stop().fadeIn(400)}).mousemove(function(t){}).mouseout(function(){n.stop().fadeOut(400)})}})}$(document).ready(function(){($("#home_dashboard").length||$("#workout_plans_show").length)&&($(".edit-day").hide(),$(".add-day").hide(),simple_tooltip(".show-tooltip","tooltip"))});